import React from "react";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export default class App extends React.Component {
  render() {
  {/* styles */}
    var inputBoxStyle = {
      width: "45vw",
      height: "60vh",
      marginLeft: "auto",
      marginRight: "auto",
      padding:"10px"
    };

    return (
      <div className="App">

        <Container fluid>

          {/* Title Row */}
          <Row>
            <Col>
              <h1 className="text-center">
                Markdown Preview Application
              </h1>
            </Col>
          </Row>

          {/* Sub-Title Row */}
          <Row>
            {/* Text Input Col */}
            <Col>
              <h3 className="text-center">
                Text Input
              </h3>
            </Col>

            {/* Preview Col */}
            <Col>
              <h3 className="text-center">
                Markdown Preview
              </h3>
            </Col>
          </Row>

          {/* Text Box Row */}
          <Row>
            {/* Text Input Col */}
            <Col>
              <div className="mark-input text-center" style={inputBoxStyle}>
                <textarea className="input" style={inputBoxStyle}>

                </textarea>
              </div>
            </Col>

            {/* Preview Col */}
            <Col>
            <div style={outputBoxStyle}>
            </div>
            </Col>
          </Row>

        </Container>

      </div>
    );
  }
}
